window.addEventListener(`TrunkApplicationStarted`,()=>{let c=3;console.log(`✅ Leptos app mounted — initializing UI scripts`);const a=document.getElementById(`menu-btn`);const b=document.getElementById(`mobile-menu`);if(a&&b){a.addEventListener(`click`,()=>{b.classList.toggle(`hidden`);console.log(`🍔 Menu toggled`)})}else{console.warn(`Menu elements not found yet.`)};if(typeof Swiper!==`undefined`){console.log(`🌀 Initializing Swiper...`);new Swiper(`.mySwiper`,{slidesPerView:c,spaceBetween:30,pagination:{el:`.swiper-pagination`,clickable:!0},breakpoints:{320:{slidesPerView:1},640:{slidesPerView:2},1024:{slidesPerView:c}}})}else{console.warn(`Swiper not loaded yet.`)}})